mapper.select:
		select barcode, bookName, bookTypeName, price, count, publishDate, publish, bookPhoto 
		from t_book inner join t_booktype on bookType=bookTypeId where barcode like '%" + #{barcode} + "%'

		String sqlAdd = "";
        if(!barcode.equals("")) {
        	sqlAdd = sqlAdd + " and book.barcode like '%" + barcode + "%'";
        }
        if(!bookName.equals("")) {
        	sqlAdd = sqlAdd + " and book.bookName like '%" + bookName + "%'";
        }
        if(0 != bookType ) {
        	sqlAdd += " and book.bookType=" + bookType;
        }
        if(!publishDate.equals("")) {
        	sqlAdd = sqlAdd + " and book.publishDate like '%" + publishDate + "%'";
        }
