mapper.select:
select xuan_ti.id, type, year, season, book_name, sub_book_name, department, originator, gao_jian_source.content as gao_jian_source, chu_shen_comments.content as chu_shen_comments, xuan_ti_status.content as xuan_ti_status, ISBN from ((xuan_ti inner join gao_jian_source on xuan_ti.source=gao_jian_source.id) inner join chu_shen_comments on xuan_ti.first_comments=chu_shen_comments.id) inner join xuan_ti_status on xuan_ti.status=xuan_ti_status.id
xuan_ti.id like CONCAT(CONCAT('%', #{xuan_ti_id, jdbcType=VARCHAR}), '%')
AND year like CONCAT(CONCAT('%', #{year, jdbcType=VARCHAR}), '%')
AND source = #{source, jdbcType=INTEGER}
AND status = #{status, jdbcType=INTEGER}
order by xuan_ti.id asc
